import React from 'react'
import { connect } from 'react-redux'
import { updateItem, removeItemFromList } from '../actions/itemActions'
import './ListItems.css'

class ListItems extends React.Component {
    render() {
        const { items, dispatchUpdateItem, dispatchRemoveItemFromList } = this.props
        return(
            <div>
                <ul>
                    {
                        items.map((item, i) => (
                            <li key={i} onClick={() => dispatchUpdateItem(item)}>
                                <p>{item.name}</p>
                                <p>{item.price}</p>
                                <p>{item.type}</p>
                                <button onClick={() => dispatchRemoveItemFromList(item)}>X</button>
                            </li>
                        ))
                    }
                </ul>
            </div>
        )
    }
}

export const mapStateToProps = state => ({
    items: state.itemsList.items
})

export const mapDispatchToProps = dispatch => ({
    dispatchUpdateItem: data => dispatch(updateItem(data)),
    dispatchRemoveItemFromList: data => dispatch(removeItemFromList(data))
})

export default connect(mapStateToProps, mapDispatchToProps)(ListItems)
