import React from 'react'
import { connect } from 'react-redux'
import { updateItem } from '../actions/itemActions'
import './ListItems.css'

class ListItems extends React.Component {

    //write as arrow function to call it with this in the return without a call back like {() => this.myFunc()}
    handleEvents = item => {
        const { dispatchUpdateItem } = this.props
        dispatchUpdateItem(item)
        dispatchUpdateNumberOfItems()
    }

    render() {
        const { items } = this.props
        return(
            <div>
                <ul>
                    {
                        items.map((item, i) => (
                            <li key={i} onClick={() => this.handleEvents(item)}>
                                <p>{item.name}</p>
                                <p>{item.price}</p>
                                <p>{item.type}</p>
                            </li>
                        ))
                    }
                </ul>
            </div>
        )
    }
}

export const mapStateToProps = state => ({
    items: state.itemsList.items
})

export const mapDispatchToProps = dispatch => ({
    dispatchUpdateItem: data => dispatch(updateItem(data)),
    dispatchUpdateNumberOfItems: () => dispatch(updateNumberOfItems())
})

export default connect(mapStateToProps, mapDispatchToProps)(ListItems)
