import React from 'react'
import { connect } from 'react-redux'
import { removeItemFromList, updateNumberOfItems } from '../actions/itemActions'
import './Cart.css'

class Cart extends React.Component {

    //write as arrow function to call it with this in the return without a call back like {() => this.myFunc()}
    handleEvents = item => {
        const { items, dispatchRemoveItemFromList, dispatchUpdateNumberOfItems } = this.props
        dispatchRemoveItemFromList(item)
        dispatchUpdateNumberOfItems(items.length)
    }

    render() {
        const { items } = this.props
        return(
            <div className='cartContainer'>
                <ul>
                    {
                        items.map((item, i) => (
                            <li key={i}>
                                <p>{item.name}</p>
                                <p>{item.price}</p>
                                <p>{item.type}</p>
                                <button onClick={() => this.handleEvents(item)}>X</button>
                            </li>
                        ))
                    }
                </ul>
            </div>
        )
    }
}

export const mapStateToProps = state => ({
    items: state.itemsList.selectedItems
})

export const mapDispsatchToProps = dispatch => ({
    dispatchRemoveItemFromList: data => dispatch(removeItemFromList(data)),
    dispatchUpdateNumberOfItems: data => dispatch(updateNumberOfItems(data))
})

export default connect(mapStateToProps, mapDispsatchToProps)(Cart)
