import React from 'react';
import './App.css';
import Cart from './cart/Cart.js'
import ListItems from './list-items/ListItems.js'
import itemsData from './dataFile'
import { setItemsToStore } from './actions/itemActions'
import { connect } from 'react-redux'

class App extends React.Component {

  componentDidMount() {
    const { dispatchSetItemsToStore } = this.props
    dispatchSetItemsToStore(itemsData)
  }

  render() {
    const { items } = this.props
    return (
      <div className="App">
        <ListItems items={items} />
        <Cart />
      </div>
  )
  }
}

export const mapStateToProps = state => ({
  items: state.itemsList.items
})

export const mapDispatchToProps = dispatch => ({
  dispatchSetItemsToStore: data => dispatch(setItemsToStore(data))
})

export default connect(mapStateToProps, mapDispatchToProps)(App)
